import React, { Component, useState, useEffect } from 'react';
import withFirebaseAuth from 'react-with-firebase-auth'
import * as firebase from 'firebase/app';
import 'firebase/auth';
import firebaseconfig from './firebaseconfig';
import './App.css';
//import {BrowserRouter as Router,Route,Redirect,Switch, Link} from 'react-router-dom';
import Contacts from './components/contacts';

const firebaseApp = firebase.initializeApp(firebaseconfig);

//Console testing for API data
const element = fetch("https://localhost:44358/api/UserInformations")
      .then((response) => response.json())
      .then((data) => console.log('Here is my data returned:', data[0].accountNumber));
//Console testing

class App extends Component{
  
  getVal(){
    const element = fetch("https://localhost:44358/api/UserInformations")
      .then((response) => response.json())
      .then((data) => console.log('Here is my data returned:', data[0].accountNumber));

      return data;
  }


  state = {
    contacts: []
  }

  componentDidMount() {
    fetch('https://localhost:44358/api/UserInformations')
    .then(res => res.json())
    .then((data) =>  {
      this.setState({ contacts: data })
    })
    .catch(console.log)
  }

  render() {
    const {
      user,
      signOut,
      signInWithGoogle,
    } = this.props;
    return (  
      <div className="App">
        <header className="App-header">
          <img src="https://media.giphy.com/media/9P3DSO2FzzvWxDtdWP/giphy.gif"/>
          {
            user
              ? <p>Hello, {user.displayName}{this.getVal()}<Contacts contacts={this.state.contacts} />
              </p>
              : <p>Please sign in.</p>   
              
          }
          {
            user
              ? <>
              <button onClick={signOut}>Sign out</button>
                </>
              : <button onClick={signInWithGoogle}>Sign in with Google</button>
          }
        </header>
      </div>
    );//End of Return
  }//End of Render()
}//End Class App

const firebaseAppAuth = firebaseApp.auth();

const providers = {
  googleProvider: new firebase.auth.GoogleAuthProvider(),
};

export default withFirebaseAuth({
  providers,
  firebaseAppAuth,
})(App);

