const [tableContent, setTableContent] = useState([]);

class App extends Component{
  
  constructor(props){
    super(props);
    this.state = {
     redirectMe: true
    }}
  
  apiUrl = 'https://localhost:44358/api/UserInformations/45505';

  onClicked = () =>{
    return <Router><Link to="/App2">REDIRECT</Link></Router>//<Redirect to = "./App2"/>
  }

  render() {

    useEffect(() => {
      fetch("https://localhost:44358/api/UserInformations")
        .then(response => response.json())
        .then(json => setTableContent(json));
    }, []);

    //const element = fetch(this.apiUrl)
    //  .then((response) => response.json())
    //  .then((data) => console.log('Here is my data returned:', data[0].accountNumber));
    const {
      user,
      signOut,
      signInWithGoogle,
    } = this.props;
    {tableContent.map(content => {
    return (  
      <div className="App">
        <header className="App-header">
          <img src="https://media.giphy.com/media/9P3DSO2FzzvWxDtdWP/giphy.gif"/>
          {
            user
              ? <p>Hello, {user.displayName}
              <FormInput size="sm" placeholder="Small input" className="mb-2" />
              </p>
              : <p>Please sign in.</p>   
              
          }

          {
            user
              ? <><button onClick={signOut}>Sign out</button>
              <button onClick={this.onClicked}>Sign out</button>
              <Router><Link to="/App2">REDIRECT</Link></Router></>
              : <button onClick={signInWithGoogle}>Sign in with Google</button>
          }
        </header>
      </div>
    )/*end of Return*/;})}
  }//End of Render()
}//End Class App

const firebaseAppAuth = firebaseApp.auth();

const providers = {
  googleProvider: new firebase.auth.GoogleAuthProvider(),
};

export default withFirebaseAuth({
  providers,
  firebaseAppAuth,
})(App);

