import React, { Component } from 'react';
import withFirebaseAuth from 'react-with-firebase-auth'
import * as firebase from 'firebase/app';
import 'firebase/auth';
import firebaseconfig from './firebaseconfig';
import logo from './logo.svg';
import './App.css';
import { FormInput } from "shards-react";

const firebaseApp = firebase.initializeApp(firebaseconfig);

class App extends Component{

  state = {
    contacts: []
  }
  
  componentDidMount() {
    fetch('https://localhost:44358/api/UserInformations')
    .then(res => res.json())
    .then((data) => {
      this.setState({ contacts: data })
    })
    .catch(console.log)
  }

  Contacts = ({ contacts }) => {
    return (
      <div>
      <center><FormInput valid placeholder="I'm valid" className="mb-2" /></center>;
        {contacts.map((contact, index) => (
          <div class="card">
              <h5 class="card-title">{contact.accountNumber}</h5>
              <h5 class="card-title">{contact.firstName}</h5>
              <h5 class="card-title">{contact.accountBalance}</h5>
              <h5 class="card-title">{contact.age}</h5>
          </div>
        ))}
      </div>
    )
  };



  apiUrl = 'https://localhost:44358/api/UserInformations';

  render() {
    //fetch(this.apiUrl)
      //.then((response) => response.json())
      //.then((data) => console.log('Here is my data returned:', firstname = data[0].accountNumber));

    const {
      user,
      signOut,
      signInWithGoogle,
    } = this.props;

    return (  
      <div className="App">
        <header className="App-header">

        
          <img src="https://images-na.ssl-images-amazon.com/images/I/71frl22CXNL._SY355_.png"/>
          {
            user
              ? <p>Hello, {user.displayName}
              
              <FormInput size="sm" placeholder="Small input" className="mb-2" />

              </p>
              : <p>Please sign in.</p>   
              
          }

          {
            user
              ? <button onClick={signOut}>Sign out</button>
              : <button onClick={signInWithGoogle}>Sign in with Google</button>
          }
        </header>
      </div>
    );
  }
}

const firebaseAppAuth = firebaseApp.auth();

const providers = {
  googleProvider: new firebase.auth.GoogleAuthProvider(),
};

export default withFirebaseAuth({
  providers,
  firebaseAppAuth,
})(App);


