import React, { Component } from 'react';
import withFirebaseAuth from 'react-with-firebase-auth'
import * as firebase from 'firebase/app';
import 'firebase/auth';
import firebaseconfig from './firebaseconfig';
import logo from './logo.svg';
import './App.css';
import { FormInput } from "shards-react";

const firebaseApp = firebase.initializeApp(firebaseconfig);

class App extends Component{

  apiUrl = 'https://localhost:44358/api/UserInformations';

  render() {
    //fetch(this.apiUrl)
      //.then((response) => response.json())
      //.then((data) => console.log('Here is my data returned:', firstname = data[0].accountNumber));
    const {
      user,
      signOut,
      signInWithGoogle,
      contacts=this.state.contacts
    } = this.props;

    return (  
      <div className="App">
        <header className="App-header">

          <img src="https://giphy.com/gifs/Z3aQVJ78mmLyo/html5"/>
          {
            user
              ? <p>Hello, {user.displayName}{contacts.firstName}
              
              <FormInput size="sm" placeholder="Small input" className="mb-2" />

              </p>
              : <p>Please sign in.</p>   
              
          }

          {
            user
              ? <button onClick={signOut}>Sign out</button>
              : <button onClick={signInWithGoogle}>Sign in with Google</button>
          }
        </header>
      </div>
    );
  }
}

const firebaseAppAuth = firebaseApp.auth();

const providers = {
  googleProvider: new firebase.auth.GoogleAuthProvider(),
};

export default withFirebaseAuth({
  providers,
  firebaseAppAuth,
})(App);


