import React, { useState } from "react";
import { useHistory, Route, Redirect, Link } from 'react-router-dom';
import { Button, FormGroup, FormControl, ControlLabel } from "react-bootstrap";
import "./Login.css";
//import { Link } from 'react-router';
//import login from "./containers/login";

export default function Login() {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");

  function validateForm() {
    return email.length > 0 && password.length > 0;
  }

  function handleSubmit(event) {
    event.preventDefault();
  }

  onSubmit = () => {
    if(userFound){
        return  <Redirect  to="src\components\contacts.js" />
    }
 }
  //const { push } = useHistory();
  //const handleClick = () => {
  //this.history.push("C:\Git\Bank\react-api\src\components\contacts.js");}

  return (
    <div className="Login">
      <form onSubmit={handleSubmit}>
        <FormGroup controlId="email" bsSize="large">
          <ControlLabel>Email</ControlLabel>
          <FormControl
            autoFocus
            type="email"
            value={email}
            onChange={e => setEmail(e.target.value)}
          />
        </FormGroup>
        <FormGroup controlId="password" bsSize="large">
          <ControlLabel>Password</ControlLabel>
          <FormControl
            value={password}
            onChange={e => setPassword(e.target.value)}
            type="password"
          />
        </FormGroup>
        <Button onClick={this.onSubmit} block bsSize="large" disabled={!validateForm()} type="submit">
        login
        </Button>
      </form>
    </div>
  );
}
