import React, { Component, useState, useEffect } from 'react';
import withFirebaseAuth from 'react-with-firebase-auth'
import * as firebase from 'firebase/app';
import 'firebase/auth';
import firebaseconfig from './firebaseconfig';
//import logo from './logo.svg';
import './App.css';
import { FormInput } from "shards-react";
import Users from './components/users'
import {BrowserRouter as Router,Route,Redirect,Switch, Link} from 'react-router-dom';

const firebaseApp = firebase.initializeApp(firebaseconfig);

class App extends Component{
  
  constructor(props){
    super(props);
    this.state = {
     redirectMe: true
    }}
  
  apiUrl = 'https://localhost:44358/api/UserInformations/45505';

  onClicked = () =>{
    return <Redirect to = "./App2"/>
  }

  render() {
    let { redirectMe } = this.state;
    //const element = fetch(this.apiUrl)
    //  .then((response) => response.json())
    //  .then((data) => console.log('Here is my data returned:', data[0].accountNumber));
    const {
      user,
      signOut,
      signInWithGoogle,
    } = this.props;
    if(redirectMe){
    return (  
      <div className="App">
        <header className="App-header">
          <img src="https://media.giphy.com/media/9P3DSO2FzzvWxDtdWP/giphy.gif"/>
          {
            user
              ? <p>Hello, {user.displayName}
              <FormInput size="sm" placeholder="Small input" className="mb-2" />
              </p>
              : <p>Please sign in.</p>   
              
          }

          {
            user
              ? <><button onClick={signOut}>Sign out</button>
              <Router><Link to="/somepage">REDIRECT</Link></Router></>
              : <button onClick={signInWithGoogle}>Sign in with Google</button>
          }
        </header>
      </div>
    );}
  }
}//End Class App

const firebaseAppAuth = firebaseApp.auth();

const providers = {
  googleProvider: new firebase.auth.GoogleAuthProvider(),
};

export default withFirebaseAuth({
  providers,
  firebaseAppAuth,
})(App);

