/**
 * The ProgressBar ref.
 */
/// <reference types="react" />
export interface ProgressBarHandle {
    /**
     * The ProgressBar element.
     */
    element: React.RefObject<HTMLDivElement | null>;
    /**
     * The progress status element.
     */
    progressStatusRef: React.RefObject<HTMLDivElement | null>;
    /**
     * The progress status wrap element.
     */
    progressStatusWrapRef: React.RefObject<HTMLSpanElement | null>;
    /**
     * Focus the ProgressBar.
     */
    focus: () => void;
}
